sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	initialize: function() {

	},
	isDatePast: function(strDate) {
		var rightNow = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);
		if (testDate.before(rightNow)) {
			gs.addErrorMessage("When Needed can't be in the past. Not saved to database");
			current.setAbortAction(true);
		}

		var today = rightNow.getLocalDate();
		var testDay = whenNeeded.getLocalDate();
		if (today.compareTo(testDay) == 0) {
			gs.addErrorMessage("When Needed can't be in today. Not saved to database");
			current.setAbortAction(true);
		}
	},
    type: 'NeedItUtils'
});
